import React from 'react'
import ReactDOM from 'react-dom'

const user = {
    name : 'Alexander',
    lastName : 'Esteban',
    avatar : 'https://scontent.feoh1-1.fna.fbcdn.net/v/t1.0-0/p480x480/50913160_2519207301440707_7588834898496978944_o.jpg?_nc_cat=102&_nc_ohc=pBKeFT08tqsAX_yNfD8&_nc_ht=scontent.feoh1-1.fna&_nc_tp=1002&oh=b1b32900abb99d6ba04a5103c389466e&oe=5E8F7281'
}

function getGretting (user){
    if (user) {
    return <h1>Hello {getName(user)}!</h1>
    }else{
        return <h1>Hello Stranger!</h1>
    }
}

function getName(user) {
    return `${user.name} ${user.lastName}`
}


const element = <div>{getGretting(user)}</div>
const img = <img src={user.avatar} style={{borderRadius:'100%'}} />
element.appendChild(img)
const container = document.querySelector('#root')
element.appendChild(img)
ReactDOM.render(img, container)
