    const [data, setData] = useState([])
    const [loading, setLoading] = useState(false)
    const [error, setError] = useState(null)

        if (loading)
            return <Loading />

        if (error) 
            return <FatalError />

        return <Exercise
            loading = {loading}
            userName = "Alexander Zapata"
            exercisesData = {data} 
            />
}
