import React from 'react'
import CircleImg from '../images/circles.png'
import './Styles/Card.css'

class Card extends React.Component{

    // constructor(props){
    //     super(props)
    //     this.state = {
    //         image: 'https://scontent.feoh1-1.fna.fbcdn.net/v/t1.0-9/p720x720/82900206_1462903780554519_7805310230991994880_o.jpg?_nc_cat=107&_nc_ohc=eyDkNRAEFX8AX-vrb5U&_nc_ht=scontent.feoh1-1.fna&_nc_tp=1002&oh=1c02a86b287172c3e89db715b01b2fda&oe=5ED2E046'
    //     }
    // }

    componentDidMount(){
        setTimeout(()=> {
            this.setState({
                image : this.props.img
            })
        }, 5000);
    }
    render(){
        const {title, description, img, leftColor, rightColor} = this.props
        return (
            <div className="card mx-auto Fitness-Card"style={{
                backgroundImage:`url(${CircleImg}), linear-gradient(to right, ${leftColor}, ${rightColor})`
            }}
            >
                <div className="card-body">
                    <div className="row center">
                        <div className="col-6">
                            <img src={this.state.image} alt="Technique Guides"/>
                        </div>
                        <div className="col-6 Fitness-Card-Info">
                            <h1>{title}</h1>
                            <p>{description}</p>
                        </div>
                    </div>
                </div>
            </div>
            
        )
    }
}

export default Card
