        await this.fetchExercises()
    }

    fetchExercises = async () =>{
        try {
            let url = 'http://localhost:8000/api/exercises'
            let response = await fetch(url)

            const data = await response.json()
            this.setState({data})
        } catch (error) {
            
        }
    }

    render(){
        return(
            <div>
                <Title
                    userName="Alex"
                />
                <ExerciseList
                    exercises= {this.state.data}
                />

               <AddButton/>
            </div>
        )
    }
}

